import { useState } from "react";
import { NavLink } from "react-router-dom";

export function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav style={{ background: "#1e1e1e", padding: "1rem" }}>
      {/* Contenedor principal */}
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        {/* Logo o nombre */}
        <h2 style={{ color: "white", margin: 0 }}>Mi Portfolio</h2>

        {/* Botón hamburguesa (solo visible en mobile) */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          style={{
            background: "transparent",
            border: "none",
            color: "white",
            fontSize: "1.5rem",
            cursor: "pointer",
            display: "none", // por defecto oculto
          }}
          className="menu-button"
        >
          ☰
        </button>

        {/* Links en horizontal (desktop) */}
        <ul
          style={{
            display: "flex",
            gap: "1.5rem",
            listStyle: "none",
            margin: 0,
            padding: 0,
          }}
          className="menu-desktop"
        >
          <li>
            <NavLink
              to="/"
              style={({ isActive }) => ({
                color: isActive ? "yellow" : "white",
                textDecoration: "none",
              })}
            >
              Home
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/Page1"
              style={({ isActive }) => ({
                color: isActive ? "yellow" : "white",
                textDecoration: "none",
              })}
            >
              Page 1
            </NavLink>
          </li>
        </ul>
      </div>

      {/* Menú desplegable en mobile */}
      {isOpen && (
        <ul
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "1rem",
            listStyle: "none",
            marginTop: "1rem",
            padding: 0,
          }}
          className="menu-mobile"
        >
          <li>
            <NavLink
              to="/"
              style={({ isActive }) => ({
                color: isActive ? "yellow" : "white",
                textDecoration: "none",
              })}
              onClick={() => setIsOpen(false)}
            >
              Home
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/Page1"
              style={({ isActive }) => ({
                color: isActive ? "yellow" : "white",
                textDecoration: "none",
              })}
              onClick={() => setIsOpen(false)}
            >
              Page 1
            </NavLink>
          </li>
        </ul>
      )}
    </nav>
  );
}
