import { useState } from "react";
import { NavLink } from "react-router-dom";

export function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav style={{ background: "#1e1e1e", padding: "1rem" }}>
      <div
        style={{
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
        }}
      >
        {/* Logo */}
        <h2 style={{ color: "white", margin: 0 }}>Mi Portfolio</h2>

        {/* Botón hamburguesa (visible siempre por ahora) */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          style={{
            background: "transparent",
            border: "none",
            color: "white",
            fontSize: "1.8rem",
            cursor: "pointer",
          }}
        >
          ☰
        </button>
      </div>

      {/* Menú desplegable */}
      {isOpen && (
        <ul
          style={{
            display: "flex",
            flexDirection: "column",
            gap: "1rem",
            listStyle: "none",
            marginTop: "1rem",
            padding: 0,
          }}
        >
          <li>
            <NavLink to="/" style={{ color: "white", textDecoration: "none" }}>
              Home
            </NavLink>
          </li>
          <li>
            <NavLink
              to="/Page1"
              style={{ color: "white", textDecoration: "none" }}
            >
              Page 1
            </NavLink>
          </li>
        </ul>
      )}
    </nav>
  );
}
