import { useState } from "react";
import { NavLink } from "react-router-dom";

export function Navbar() {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav style={{ background: "#1e1e1e", padding: "1rem" }}>
      <div
        style={{
          display: "flex",
          justifyContent: "flex-end",
          alignItems: "center",
        }}
      >
        {/* Botón hamburguesa (solo en móvil con CSS) */}
        <button
          onClick={() => setIsOpen(!isOpen)}
          className="menu-btn"
        >
          ☰
        </button>
      </div>

      {/* Menú */}
      <ul className={`menu ${isOpen ? "open" : ""}`}>
        <li>
          <NavLink to="/" className="menu-link">
            Home
          </NavLink>
        </li>
        <li>
          <NavLink to="/Page1" className="menu-link">
            Page 1
          </NavLink>
        </li>
      </ul>

      {/* Estilos */}
      <style>{`
        .menu {
          display: flex;
          gap: 1rem;
          list-style: none;
          margin: 0;
          padding: 0;
        }

        .menu-link {
          color: white;
          text-decoration: none;
          background: #333;
          padding: 0.5rem 1rem;
          border-radius: 8px;
        }

        .menu-btn {
          display: none;
          background: transparent;
          border: none;
          color: white;
          font-size: 1.8rem;
          cursor: pointer;
        }

        /* En móviles */
        @media (max-width: 768px) {
          .menu {
            display: none;
            flex-direction: column;
            margin-top: 1rem;
          }

          .menu.open {
            display: flex;
          }

          .menu-btn {
            display: block;
          }
        }
      `}</style>
    </nav>
  );
}
