import React, { useState } from "react";
import {
  DndContext,
  closestCenter,
  PointerSensor,
  useSensor,
  useSensors,
} from "@dnd-kit/core";
import {
  arrayMove,
  SortableContext,
  useSortable,
  verticalListSortingStrategy,
} from "@dnd-kit/sortable";
import { CSS } from "@dnd-kit/utilities";
import { GripVertical, Trash2, Plus, Minus } from "lucide-react";

// Componente SortableItem (cada Task)
function SortableItem({ id, name, valor, onDelete, onIncrement, onDecrement }) {
  const { attributes, listeners, setNodeRef, transform, transition } =
    useSortable({ id });

  const baseHeight = 42; // altura base para 1 hora
  const style = {
    transform: CSS.Transform.toString(transform),
    transition, // no animamos height, solo posición
    background: "#f2f2f3",
    padding: "0 15px",
    borderRadius: "5px",
    display: "flex",
    justifyContent: "space-between",
    alignItems: "flex-start", // contenido alineado arriba
    color: "blue",
    marginBottom: "0px",
    height: `${baseHeight * valor}px`,
    boxSizing: "border-box",
    outline: "1px dotted black", // ✅ outline punteado negro
  };

  return (
    <div ref={setNodeRef} style={style}>
      {/* Icono DnD */}
      <div
        {...attributes}
        {...listeners}
        style={{ cursor: "grab", marginRight: "8px" }}
      >
        <GripVertical size={18} /> {/* ✅ icono de puntitos */}
      </div>

      {/* Nombre empleado */}
      <div style={{ flex: 1, alignSelf: "flex-start" }}>{name}</div>

      {/* Botones - / + y horas */}
      <div style={{ display: "flex", alignItems: "center", gap: "4px" }}>
        <button
          onClick={() => onDecrement(id)}
          style={{ border: "none", background: "transparent", cursor: "pointer" }}
        >
          <Minus size={16} />
        </button>
        <span>{valor}h</span>
        <button
          onClick={() => onIncrement(id)}
          style={{ border: "none", background: "transparent", cursor: "pointer" }}
        >
          <Plus size={16} />
        </button>
        <button
          onClick={() => onDelete(id)}
          style={{ border: "none", background: "transparent", cursor: "pointer" }}
        >
          <Trash2 size={16} />
        </button>
      </div>
    </div>
  );
}

// Input para agregar empleados
function Input({ onSubmit }) {
  const [value, setValue] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (value.trim()) {
      onSubmit(value.trim());
      setValue("");
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ display: "flex", gap: "10px" }}>
      <input
        type="text"
        placeholder="Employee name"
        value={value}
        onChange={(e) => setValue(e.target.value)}
        style={{ flex: 1 }}
      />
      <button type="submit">Add Employee</button> {/* ✅ texto cambiado */}
    </form>
  );
}

// Timeline de 24h
function Timeline() {
  const hours = [];
  for (let i = 6; i < 30; i++) {
    const hour = i % 24;
    const label =
      hour === 0
        ? "12am"
        : hour < 12
        ? `${hour}am`
        : hour === 12
        ? "12pm"
        : `${hour - 12}pm`;

    let background = "yellow"; // 6am–11am
    if (hour >= 12 && hour <= 23) background = "orange"; // 12pm–11pm
    if (hour >= 0 && hour <= 5) background = "pink"; // 12am–5am

    hours.push(
      <div
        key={i}
        style={{
          height: "42px", // altura base
          display: "flex",
          alignItems: "flex-start",
          paddingLeft: "5px",
          fontSize: "12px",
          color: "yellow", // texto amarillo
          background,
          borderBottom: "1px solid #ccc",
        }}
      >
        {label}
      </div>
    );
  }

  return (
    <div
      style={{
        width: "60px",
        borderRight: "1px solid #ccc",
        background: "#333",
      }}
    >
      {hours}
    </div>
  );
}

// Home
export default function Home() {
  const [tasks, setTasks] = useState([]);
  const sensors = useSensors(useSensor(PointerSensor));

  const addTask = (name) => {
    const newTask = {
      id: tasks.length + 1,
      name,
      valor: 1, // ✅ empieza con 1h
    };
    setTasks([...tasks, newTask]);
  };

  const deleteTask = (id) => {
    setTasks(tasks.filter((task) => task.id !== id));
  };

  const incrementTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id && task.valor < 8
          ? { ...task, valor: task.valor + 1 }
          : task
      )
    );
  };

  const decrementTask = (id) => {
    setTasks(
      tasks.map((task) =>
        task.id === id && task.valor > 1
          ? { ...task, valor: task.valor - 1 }
          : task
      )
    );
  };

  const handleDragEnd = (event) => {
    const { active, over } = event;
    if (active.id !== over.id) {
      setTasks((items) => {
        const oldIndex = items.findIndex((item) => item.id === active.id);
        const newIndex = items.findIndex((item) => item.id === over.id);
        return arrayMove(items, oldIndex, newIndex);
      });
    }
  };

  return (
    <div style={{ display: "flex" }}>
      <Timeline />
      <div style={{ flex: 1, padding: "15px" }}>
        <Input onSubmit={addTask} />
        <DndContext
          sensors={sensors}
          collisionDetection={closestCenter}
          onDragEnd={handleDragEnd}
        >
          <SortableContext
            items={tasks.map((task) => task.id)}
            strategy={verticalListSortingStrategy}
          >
            {tasks.map((task) => (
              <SortableItem
                key={task.id}
                id={task.id}
                name={task.name}
                valor={task.valor}
                onDelete={deleteTask}
                onIncrement={incrementTask}
                onDecrement={decrementTask}
              />
            ))}
          </SortableContext>
        </DndContext>
      </div>
    </div>
  );
}
